mapper.insert:
    	INSERT INTO ilban_holiday (holiday_number
			,appointment_school_personnel_number
			,holiday_name
			,holiday_category
			,holiday_start_day
			,holiday_end_day
			,holiday_registration_day
			,holiday_modification_date)
		VALUES((SELECT MAX(holiday_number+1) AS holiday_number FROM ilban_holiday ALIAS_FOR_SUBQUERY)
			,#{personnelNumber}
			,#{holidayName}
			,#{holidayCategory}
			,#{holidayStartDay}
			,#{holidayEndDay}
			,LEFT(now(), 10)
			,LEFT(now(), 10) )
